package com.power.doc.util;import com.power.common.model.CommonResult;import com.power.doc.enums.ErrorCodeEnum;/** * 公共响应结果成功失败的静态方法调用 * * @author yu */public class ResultUtil {    /**     * 返回成功结果     *     * @param data     * @return     */    public static <T> CommonResult<T> returnSuccess(T data) {        CommonResult<T> result = new CommonResult();        result.setCode(ErrorCodeEnum.SUCCESS.getCode());        result.setSuccess(true);        result.setData(data);        result.setMessage(ErrorCodeEnum.SUCCESS.getDesc());        return result;    }    /**     * 返回错误信息     *     * @param msg     * @param code     * @return     */    public static CommonResult returnError(String msg, String code) {        CommonResult result = new CommonResult();        result.setCode(code);        result.setData("");        result.setMessage(msg);        return result;    }    /**     * 使用状态码枚举类     *     * @param status     * @return     */    public static CommonResult returnError(ErrorCodeEnum status) {        return returnError(status.getDesc(), status.getCode());    }    /**     * 必选参数验证返回     *     * @return     */    public static CommonResult returnParamNull() {        CommonResult result = new CommonResult();        result.setCode(ErrorCodeEnum.PARAM_EMPTY.getCode());        result.setData("");        result.setMessage(ErrorCodeEnum.PARAM_EMPTY.getDesc());        return result;    }}